function CheckProgramInstallation($ProgramName) { $registryPaths = @( "HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall", "HKLM:\Software\WOW6432Node\Microsoft\Windows\CurrentVersion\Uninstall", # Combine paths for 64-bit, 32-bit, and current user registries "HKCU:\Software\Microsoft\Windows\CurrentVersion\Uninstall" ) foreach ($path in $registryPaths) { # Iterate through each registry path try { $result = Get-ChildItem -Path $path -ErrorAction SilentlyContinue | Where-Object { (Get-ItemProperty -Path $_.PSPath).DisplayName -like "*$ProgramName*" } if ($result) { return $true } } catch { continue } # Skip paths that are inaccessible } return $false }
function Test-InternetConnection { $retries = 5 $success = $false
for ($i = 0; $i -lt $retries; $i++) { # Try to ping google.com first try { $pingResult = Test-Connection -ComputerName google.com -Count 1 -Quiet if ($pingResult) { $success = $true break } } catch {} Start-Sleep -Seconds 1 } if (-not $success) { # Only if google.com is unreachable, try pinging outlook.com as a backup measure for ($i = 0; $i -lt $retries; $i++) { try { $pingResult = Test-Connection -ComputerName outlook.com -Count 1 -Quiet if ($pingResult) { $success = $true break } } catch {} Start-Sleep -Seconds 1 } } return $success }
function Retry-RemoveItem { param ( [Parameter(Mandatory = $true)] [string]$Path, [int]$MaxRetries = 20, # Maximum number of retry attempts [int]$DelaySeconds = 2 # Delay between retries in seconds ) $retryCount = 0 while ($retryCount -lt $MaxRetries) { try { Remove-Item -Path $Path -Force -ErrorAction Stop return } catch { Start-Sleep -Seconds $DelaySeconds $retryCount++ } } Write-Warning "Failed to remove file after $MaxRetries attempts: $Path" }
