function Winget-Install { param( [string]$appName, [string]$wingetID ) if ($DiscordBox.IsChecked) { Get-Process -Name "*Discord*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue } if ($powertoysBox.IsChecked) { Get-Process -Name "*PowerToys*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue } # if by chance any of these windows are still open in background from the installations, then close them so cleaner if ($hwinfoBox.IsChecked) { Get-Process -Name "*HWiNFO*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue } if ($tailscaleBox.IsChecked) { Get-Process -Name "*TailScale*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue } if ($playniteBox.IsChecked) { Get-Process -Name "*Playnite*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue }
Write-Host "`nUsing winget to install $appName. Starting installation..." Start-Sleep -Seconds 1
$tries = 0 while ($tries -lt 3) { try { $process = Start-Process winget -ArgumentList "install --id=$wingetID -e --silent --force --verbose --accept-source-agreements --accept-package-agreements" -WindowStyle Hidden -PassThru -ErrorAction Stop $process | Wait-Process Start-Sleep -Seconds 1
if ($process.ExitCode -eq 0) { Write-Host "$appName has been installed successfully!" return 1 } elseif ($process.ExitCode -eq -1978334967) { Write-Host "$appName has been installed successfully! A system restart is required though ;)" return 1 } elseif ($process.ExitCode -eq -1978335215) { Write-Host "$appName can't be installed! The hash in winget DB is not verified yet, skipping..." return 0 } else { Write-Host "Attempt $($tries + 1): Winget install (with ID) failed with exit code $($process.ExitCode)..." } } catch { Write-Host "Attempt $($tries + 1): Exception occurred while installing $appName (with ID) $($_.Exception.Message)..." } $tries++ }
$global:no_issues = 0 Write-Host "Winget install failed for $appName after 3 attempts using ID: $wingetID" -ForegroundColor Red # After all retries fail return 0 }
foreach ($appToPotentiallyInstall in $optionBoxes.Keys) { # apps installation occurs here!!!! if ($optionBoxes[$appToPotentiallyInstall].checkBox.IsChecked) { if (-not (CheckProgramInstallation $appToPotentiallyInstall)) { $result = Winget-Install $appToPotentiallyInstall $optionBoxes[$appToPotentiallyInstall].wingetID
if ($appToPotentiallyInstall -eq "Google Chrome") { # Custom measures just for this app!!!!! if (($result -eq 0) -or ($result -eq $null)) { #Backup measure to attempt installing chrome if winget doesnt work and there is internet connection still Start-Sleep -Seconds 5 if (Test-InternetConnection) { Write-Host "Internet connection still exists!`nAttempting to install $appToPotentiallyInstall again but through the web..." try { $Url = "https://dl.google.com/dl/chrome/install/googlechromestandaloneenterprise64.msi" # Define the URL $destinationFolderPath = "$env:userprofile\Downloads\ChromeStandaloneSetup64.msi" # Where the .MSI file will download Start-BitsTransfer -Source $Url -Destination $destinationFolderPath -ErrorAction Stop # Download the MSI Write-Host "$appToPotentiallyInstall file copied successfully from the Web. Starting silent installation..."
$process = Start-Process msiexec.exe -ArgumentList "/i `"$destinationFolderPath`" /quiet /norestart" -Verb RunAs -PassThru $process | Wait-Process Start-Sleep -Seconds 2 Write-Host "$appToPotentiallyInstall has been installed silently." Retry-RemoveItem -Path $destinationFolderPath -MaxRetries 20 -DelaySeconds 2
} catch { Write-Host "Still could not install $appToPotentiallyInstall even through the web...`nFailed to download or install $appToPotentiallyInstall... $_.Exception.Message" $global:no_issues = 0 } } } Write-Host "Installing some Ad-blocking extensions through Group Policy..." $null = gpupdate /force New-Item -Path "HKCU:\Software\Policies\Google\Chrome\ExtensionInstallForcelist" -Force | Out-Null #Create the regkey path first New-ItemProperty -Path "HKCU:\Software\Policies\Google\Chrome\ExtensionInstallForcelist" -Name "2" -Value "ddkjiahejlhfcafbddmgiahcphecmpfh;https://clients2.google.com/service/update2/crx" -PropertyType String -Force | Out-Null #ublock origin lite New-ItemProperty -Path "HKCU:\Software\Policies\Google\Chrome\ExtensionInstallForcelist" -Name "3" -Value "gighmmpiobklfepjocnamgkkbiglidom;https://clients2.google.com/service/update2/crx" -PropertyType String -Force | Out-Null #adblock $null = gpupdate /force #forcefully update group policy so chrome installs these extensions next time its opened }
if ($appToPotentiallyInstall -eq "Mozilla Firefox") { # Custom measures just for this app!!!!! if (($result -eq 0) -or ($result -eq $null)) { #Backup measure to attempt install if winget doesnt work and there is internet connection still Start-Sleep -Seconds 5 if (Test-InternetConnection) { Write-Host "Internet connection still exists!`nAttempting to install $appToPotentiallyInstall again but through the web..." try { $Url = "https://download.mozilla.org/?product=firefox-latest-ssl&os=win64&lang=en-US" # Define the URL $destinationFolderPath = "$env:userprofile\Downloads\Firefox.exe" # Where the file will download Start-BitsTransfer -Source $Url -Destination $destinationFolderPath -ErrorAction Stop # Download the MSI Write-Host "$appToPotentiallyInstall file copied successfully from the Web. Starting silent installation..."
$process = Start-Process -FilePath $destinationFolderPath -ArgumentList "/S" -Verb RunAs -PassThru $process | Wait-Process Start-Sleep -Seconds 2 Write-Host "$appToPotentiallyInstall has been installed silently." Retry-RemoveItem -Path $destinationFolderPath -MaxRetries 20 -DelaySeconds 2
} catch { Write-Host "Still could not install $appToPotentiallyInstall even through the web...`nFailed to download or install $appToPotentiallyInstall... $_.Exception.Message" $global:no_issues = 0 } } } Write-Host "Installing uBlock Origin extension through Group Policy..." $null = gpupdate /force New-Item -Path "HKCU:\Software\Policies\Mozilla\Firefox\Extensions\Install" -Force | Out-Null #Create the regkey path first New-ItemProperty -Path "HKCU:\Software\Policies\Mozilla\Firefox\Extensions\Install" -Name "1" -Value "https://addons.mozilla.org/firefox/downloads/latest/ublock-origin/addon-607454-latest.xpi" -PropertyType String -Force | Out-Null #ublock origin $null = gpupdate /force #forcefully update group policy so firefox installs it next time its opened }
if ($appToPotentiallyInstall -eq "Discord") { # Custom measures just for this app!!!!! if (($result -eq 0) -or ($result -eq $null)) { #Backup measure to attempt install if winget doesnt work and there is internet connection still Start-Sleep -Seconds 5 if (Test-InternetConnection) { Write-Host "Internet connection still exists!`nAttempting to install $appToPotentiallyInstall again but through the web..." try { $Url = "https://discord.com/api/downloads/distributions/app/installers/latest?channel=stable&platform=win&arch=x64" # Define the UR $destinationFolderPath = "$env:userprofile\Downloads\DiscordSetup.exe" # Where the file will download Start-BitsTransfer -Source $Url -Destination $destinationFolderPath -ErrorAction Stop # Download the MSI Write-Host "$appToPotentiallyInstall file copied successfully from the Web. Starting silent installation..."
$process = Start-Process -FilePath $destinationFolderPath -ArgumentList "/S" -Verb RunAs -PassThru $process | Wait-Process Start-Sleep -Seconds 2 Write-Host "$appToPotentiallyInstall has been installed silently." Retry-RemoveItem -Path $destinationFolderPath -MaxRetries 20 -DelaySeconds 2 Start-Sleep -Seconds 2 Write-Host "Forcefully removing Discord from System Startup. To re-enable, toggle OFF and then ON in Discord settings..."
} catch { Write-Host "Still could not install $appToPotentiallyInstall even through the web...`nFailed to download or install $appToPotentiallyInstall... $_.Exception.Message" $global:no_issues = 0 } } } else { Write-Host "Forcefully removing Discord from System Startup. To re-enable, toggle OFF and then ON in Discord settings..." }
Start-Sleep -Seconds 8 Get-Process -Name "*Discord*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue if (Get-ItemProperty -Path "HKCU:\Software\Microsoft\Windows\CurrentVersion\Run" -Name "Discord" -ErrorAction SilentlyContinue) { Remove-ItemProperty -Path "HKCU:\Software\Microsoft\Windows\CurrentVersion\Run" -Name "Discord" -Force } if (Get-ItemProperty -Path "HKLM:\Software\Microsoft\Windows\CurrentVersion\Run" -Name "Discord" -ErrorAction SilentlyContinue) { Remove-ItemProperty -Path "HKLM:\Software\Microsoft\Windows\CurrentVersion\Run" -Name "Discord" -Force } }
if ($appToPotentiallyInstall -eq "PowerToys") { # Custom measures just for this app!!!!! Get-Process -Name "*PowerToys*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue }
if ($appToPotentiallyInstall -eq "HWiNFO") { # Custom measures just for this app!!!!! Get-Process -Name "*HWiNFO*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue }
if ($appToPotentiallyInstall -eq "TailScale") { # Custom measures just for this app!!!!! Get-Process -Name "*TailScale*" -ErrorAction SilentlyContinue | Stop-Process -Force -Erroraction SilentlyContinue }
